from fastapi import APIRouter, HTTPException, status
from typing import List
from database import get_db
from models import Task, TaskCreate, TaskUpdate
import crud

router = APIRouter()


@router.post("/api/tasks", response_model=Task, status_code=status.HTTP_201_CREATED)
async def create_task(task: TaskCreate):
    """Create a new task."""
    try:
        with get_db() as conn:
            task_id = crud.create_task(conn, task)
            created_task = crud.get_task_by_id(conn, task_id)

            if not created_task:
                raise HTTPException(
                    status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
                    detail="Failed to create task"
                )

            return created_task
    except Exception as e:
        raise HTTPException(
            status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
            detail=f"Error creating task: {str(e)}"
        )


@router.get("/api/tasks", response_model=List[Task])
async def get_tasks():
    """Get all tasks for the week."""
    try:
        with get_db() as conn:
            tasks = crud.get_all_tasks(conn)
            return tasks
    except Exception as e:
        raise HTTPException(
            status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
            detail=f"Error retrieving tasks: {str(e)}"
        )


@router.patch("/api/tasks/{task_id}", response_model=Task)
async def update_task(task_id: int, task_update: TaskUpdate):
    """Update an existing task."""
    try:
        with get_db() as conn:
            # Check if task exists
            existing_task = crud.get_task_by_id(conn, task_id)
            if not existing_task:
                raise HTTPException(
                    status_code=status.HTTP_404_NOT_FOUND,
                    detail=f"Task with id {task_id} not found"
                )

            # Update the task
            success = crud.update_task(conn, task_id, task_update)
            if not success:
                raise HTTPException(
                    status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
                    detail="Failed to update task"
                )

            # Return updated task
            updated_task = crud.get_task_by_id(conn, task_id)
            return updated_task
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(
            status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
            detail=f"Error updating task: {str(e)}"
        )


@router.delete("/api/tasks/{task_id}", status_code=status.HTTP_204_NO_CONTENT)
async def delete_task(task_id: int):
    """Delete a task."""
    try:
        with get_db() as conn:
            # Check if task exists
            existing_task = crud.get_task_by_id(conn, task_id)
            if not existing_task:
                raise HTTPException(
                    status_code=status.HTTP_404_NOT_FOUND,
                    detail=f"Task with id {task_id} not found"
                )

            # Delete the task
            success = crud.delete_task(conn, task_id)
            if not success:
                raise HTTPException(
                    status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
                    detail="Failed to delete task"
                )
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(
            status_code=status.HTTP_500_INTERNAL_SERVER_ERROR,
            detail=f"Error deleting task: {str(e)}"
        )

