.day-date {
  text-align: center;
  padding: 0.75rem;
  background-color: #f8f9fa;
  border-bottom: 1px solid #e0e0e0;
}

.day-date span {
  font-size: 0.875rem;
  color: #666;
  font-weight: 500;
}

