.tasks-container {
  flex: 1;
  padding: 1rem;
  min-height: 200px;
  background-color: #fafafa;
}

.no-tasks {
  text-align: center;
  color: #999;
  font-size: 0.875rem;
  font-style: italic;
  margin: 2rem 0;
}

