    .indexOf(task.day_of_week);
  const monday = this.getMonday(new Date());
  const taskDate = new Date(monday);
  taskDate.setDate(monday.getDate() + dayIndex);

  this.newTask = {
    date: taskDate,
    title: task.title,
    description: task.description || '',
    task_type: task.task_type
  };
